{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"fertest.py","provenance":[],"collapsed_sections":[]},"kernelspec":{"name":"python3","display_name":"Python 3"}},"cells":[{"cell_type":"code","metadata":{"id":"BmSgh5cu-cat","colab_type":"code","colab":{}},"source":["# load json and create model\n","from __future__ import division\n","from keras.models import Sequential\n","from keras.layers import Dense\n","from keras.models import model_from_json\n","import numpy\n","import os\n","import numpy as np\n","\n","json_file = open('fer.json', 'r')\n","loaded_model_json = json_file.read()\n","json_file.close()\n","loaded_model = model_from_json(loaded_model_json)\n","# load weights into new model\n","loaded_model.load_weights(\"fer.h5\")\n","print(\"Loaded model from disk\")\n","\n","truey=[]\n","predy=[]\n","x = np.load('./modelXtest.npy')\n","y = np.load('./modelytest.npy')\n","\n","yhat= loaded_model.predict(x)\n","yh = yhat.tolist()\n","yt = y.tolist()\n","count = 0\n","\n","for i in range(len(y)):\n","    yy = max(yh[i])\n","    yyt = max(yt[i])\n","    predy.append(yh[i].index(yy))\n","    truey.append(yt[i].index(yyt))\n","    if(yh[i].index(yy)== yt[i].index(yyt)):\n","        count+=1\n","\n","acc = (count/len(y))*100\n","\n","#saving values for confusion matrix and analysis\n","np.save('truey', truey)\n","np.save('predy', predy)\n","print(\"Predicted and true label values saved\")\n","print(\"Accuracy on test set :\"+str(acc)+\"%\")"],"execution_count":0,"outputs":[]}]}